import{_ as n,V as o,W as r,Z as l,Y as a,a1 as t,$ as i,a0 as u,F as d}from"./framework-afccc25a.js";const s={},h=l("h1",{id:"osi七层模型",tabindex:"-1"},[l("a",{class:"header-anchor",href:"#osi七层模型","aria-hidden":"true"},"#"),i(" OSI七层模型")],-1),c={class:"table-of-contents"},p=u('<h2 id="osi模型简介" tabindex="-1"><a class="header-anchor" href="#osi模型简介" aria-hidden="true">#</a> OSI模型简介</h2><h3 id="osi七层模型定义" tabindex="-1"><a class="header-anchor" href="#osi七层模型定义" aria-hidden="true">#</a> OSI七层模型定义</h3><ul><li>(ISO/IEC 7498-1)</li><li>开放式系统互联模型(Open System Interconnection Model)</li><li>世界范围内的网络标准概念模型</li><li>OSI的努力让互联网协议逐渐走向标准化</li></ul><h3 id="国际标准化组织-iso" tabindex="-1"><a class="header-anchor" href="#国际标准化组织-iso" aria-hidden="true">#</a> 国际标准化组织(ISO)</h3><ul><li>源于欧洲</li></ul><h2 id="osi七层模型具体内容" tabindex="-1"><a class="header-anchor" href="#osi七层模型具体内容" aria-hidden="true">#</a> OSI七层模型具体内容</h2><ul><li><p>应用层(Application Layer)</p><ul><li>提供高级API：定义了网络主机提供的方法和接口（业务协议、高级协议等）</li><li>往往直接对应用户行为。例如：HTTP、FTP、SMTP等</li></ul></li><li><p>展示层(Presensation Layer)</p><ul><li>也被称为语法层(Syntax Layer)</li><li>将Application Layer中的数据转化为传输格式，保留语义(如：序列化、加密解密、字符串编码解码等)</li><li>确保数据发送取出后可以被接受者理解</li></ul></li><li><p>会话层(Session Layer)</p><ul><li>提供管理会话的方法(Open/Close/ReOpen/检查状态等)</li><li>提供对底层连接断断续续的隐藏；甚至对多种底层流的隐藏（提供数据同步点）</li></ul></li><li><p>传输层(Transport Layer)</p><ul><li>提供主机到主机（host-to-host）的数据通信能力</li><li>建立连接保证数据封包发送、接收到的顺序一致</li><li>提供可靠性（发送者知道数据有没有被完整送达）</li><li>提供流控制（发送者和接收者同步速率）</li><li>提供多路复用(「多工」，Multiplexing，多种信号复用一个信道)</li></ul></li><li><p>网络层(Network Layer)</p><ul><li>提供数据在逻辑单元（例如IP地址）之间的传递能力</li><li>路由：决定数据的下一站在哪里</li><li>寻址：为数据封包增加头信息（地址等）</li></ul></li><li><p>数据链路层(Data Link Layer)</p><ul><li>提供数据在设备和设备间的传输能力</li><li>流控制：发送者接受者之间同步数据收发速度和数据量</li><li>封装了大量底层物理设备的能力</li></ul></li></ul><h2 id="结合实际应用思考" tabindex="-1"><a class="header-anchor" href="#结合实际应用思考" aria-hidden="true">#</a> 结合实际应用思考</h2><h3 id="story-当alice向bob发微信" tabindex="-1"><a class="header-anchor" href="#story-当alice向bob发微信" aria-hidden="true">#</a> Story - 当Alice向Bob发微信</h3><ul><li>Alice提交的输入被微信存储成某种内部协议格式 ---- 应用层</li><li>数据被转换成为传输用的格式（如加密、压缩等）---- 表示层</li><li>微信客户端建立到服务器的连接（也可能之前就建立好了）---- 会话层</li><li>微信客户端向服务器传输数据 ---- 传输层</li><li>一个个数据封包从主机传输到服务器 ---- 网络层</li><li>数据帧在一个个设备之间传输 ---- 数据链路层</li><li>数据最终以光电信号的形式在物理设备间传输 ---- 物理层</li></ul><h2 id="分层架构" tabindex="-1"><a class="header-anchor" href="#分层架构" aria-hidden="true">#</a> 分层架构</h2><h3 id="数据视角" tabindex="-1"><a class="header-anchor" href="#数据视角" aria-hidden="true">#</a> 数据视角</h3><ul><li><p>用户数据</p><ul><li>应用层</li><li>展示层</li><li>会话层</li></ul></li><li><p>数据封包</p><ul><li>传输层</li></ul></li><li><p>帧帧帧...</p><ul><li>网络层</li></ul></li><li><p>二进制</p><ul><li>数据链路层</li></ul></li><li><p>物理信号</p><ul><li>物理层</li></ul></li></ul><h3 id="分层架构具体实践" tabindex="-1"><a class="header-anchor" href="#分层架构具体实践" aria-hidden="true">#</a> 分层架构具体实践</h3><ul><li>把应用分成N层</li><li>替换其中一层不影响其他层的工作，不需要重构整个应用</li><li>设计要点 <ul><li>必要性/独立性/可替换</li><li>层的颗粒度</li><li>数据可追踪性</li></ul></li></ul><h2 id="课程小结" tabindex="-1"><a class="header-anchor" href="#课程小结" aria-hidden="true">#</a> 课程小结</h2><ul><li>OSI是概念模型，同时也是标准</li><li>「分层」是最常见的架构手段</li><li>后续重点学习的TCP/IP协议群（互联网协议群）受到OSI的影响</li></ul>',17);function _(f,S){const e=d("router-link");return o(),r("div",null,[h,l("nav",c,[l("ul",null,[l("li",null,[a(e,{to:"#osi模型简介"},{default:t(()=>[i("OSI模型简介")]),_:1}),l("ul",null,[l("li",null,[a(e,{to:"#osi七层模型定义"},{default:t(()=>[i("OSI七层模型定义")]),_:1})]),l("li",null,[a(e,{to:"#国际标准化组织-iso"},{default:t(()=>[i("国际标准化组织(ISO)")]),_:1})])])]),l("li",null,[a(e,{to:"#osi七层模型具体内容"},{default:t(()=>[i("OSI七层模型具体内容")]),_:1})]),l("li",null,[a(e,{to:"#结合实际应用思考"},{default:t(()=>[i("结合实际应用思考")]),_:1}),l("ul",null,[l("li",null,[a(e,{to:"#story-当alice向bob发微信"},{default:t(()=>[i("Story - 当Alice向Bob发微信")]),_:1})])])]),l("li",null,[a(e,{to:"#分层架构"},{default:t(()=>[i("分层架构")]),_:1}),l("ul",null,[l("li",null,[a(e,{to:"#数据视角"},{default:t(()=>[i("数据视角")]),_:1})]),l("li",null,[a(e,{to:"#分层架构具体实践"},{default:t(()=>[i("分层架构具体实践")]),_:1})])])]),l("li",null,[a(e,{to:"#课程小结"},{default:t(()=>[i("课程小结")]),_:1})])])]),p])}const I=n(s,[["render",_],["__file","open-system-interconnection-model.html.vue"]]);export{I as default};
